CREATE  PROCEDURE `sp_Order`(

	IN `@compCd` int(11),

	IN `@orderCd` VARCHAR(20),

	IN `@buyOrderCd` varchar(50),

	IN `@buyerNm` varchar(50),

	IN `@buyerPhone` varchar(50),

	IN `@buyerAddr` varchar(200),

	IN `@orderDt` varchar(10),

	IN `@deliveryDt` varchar(10),

	IN `@deliveryCd` varchar(50),

	IN `@deliveryCompany` varchar(5),

	IN `@deliveryStatus` varchar(5),

	IN `@remarks` varchar(4000),

	IN `@flagYN` varchar(1),

	IN `@issueID` varchar(20),

	OUT `@rsSeq` VARCHAR(20)

)
BEGIN

	DECLARE `@isCd` INTEGER DEFAULT 0;

	DECLARE `@curDt` VARCHAR(6) DEFAULT '';

	DECLARE `@isOrder` INTEGER DEFAULT 0;

	DECLARE `@seq` VARCHAR(5) DEFAULT '';

	

	/*

	SELECT CONCAT('O',DATE_FORMAT(now(), '%y%m%d'))

	DECLARE `@aa` INTEGER DEFAULT = 5;

	SELECT LPAD(`@aa`, '4' , '0');

	*/

	

	SELECT IFNULL(COUNT(*),0) INTO `@isCd` 

	FROM tblOrder 

	WHERE orderCd=`@orderCd` AND compCd=`@compCd`;

	

	IF `@orderCd` = '' THEN

		BEGIN

			SELECT DATE_FORMAT(now(), '%y%m%d') INTO `@curDt`;

			

			SELECT (COUNT(*) + 1) INTO `@isOrder` FROM tblOrder WHERE orderCd LIKE CONCAT('O',DATE_FORMAT(now(), '%y%m%d'),'%');

			

			SELECT LPAD(`@isOrder`, '5', '0') INTO `@seq`;

			

			SET `@orderCd` = CONCAT('O',`@curDt`,'-',`@seq`);

		END;

	END IF;

		

	IF `@isCd`=0 THEN

    INSERT INTO tblOrder (

      compCd

		, orderCd

		, buyOrderCd

		, buyerNm

		, buyerPhone		

		, buyerAddr

		, orderDt

		, deliveryDt

		, deliveryCd

		, deliveryCompany		

		, deliveryStatus

		, remarks

		, flagYN		

		, regDate

      , issueDate

      , issueID

    )

    VALUES (

      `@compCd`

		, `@orderCd`

		, `@buyOrderCd`

		, `@buyerNm`

		, `@buyerPhone`

		, `@buyerAddr`

		, `@orderDt`

		, `@deliveryDt`

		, `@deliveryCd`

		, `@deliveryCompany`

		, `@deliveryStatus`

		, `@remarks`

		, `@flagYN`

		, NOW()

		, NOW()

      , `@issueID`     

    );

    	 

	 ELSE

    UPDATE

      tblOrder

    SET

      buyOrderCd=`@buyOrderCd`

		, buyerNm=`@buyerNm`

		, buyerPhone=`@buyerPhone`

		, buyerAddr=`@buyerAddr`

		, orderDt=`@orderDt`

		, deliveryDt=`@deliveryDt`

		, deliveryCd=`@deliveryCd`

		, deliveryCompany=`@deliveryCompany`

		, deliveryStatus=`@deliveryStatus`

		, remarks=`@remarks`

		, flagYN=`@flagYN`

      , issueDate=NOW()

		, issueID=`@issueID`

    WHERE compCd=`@compCd` AND orderCd=`@orderCd`;



	 END IF;

	 

    SELECT `@orderCd` INTO `@rsSeq`;

	 

END ;;
