CREATE  PROCEDURE `sp_UseUpdate`()
BEGIN

	

	UPDATE tblProductInOut SET useYN = 'N', useYDate=NOW(), issueDate=NOW() WHERE inOutSeq 

	IN(SELECT A.inOutSeq

		FROM (SELECT * FROM tblProductInOut WHERE planYN = 'N' AND qty>0 AND flagYN='Y') A

		     LEFT JOIN (SELECT refSeq, SUM(qty) outQty FROM tblProductInOut 

		                  WHERE planYN = 'N' AND qty<0 AND flagYN='Y'

		                  GROUP BY refSeq

