		     LEFT JOIN tblProduct B ON A.prodCd = B.prodCd

		WHERE A.qty+IFNULL(R.outQty,0) > 0 AND A.limitDt != "" AND A.limitDt <= CURDATE()) AND useYN != 'N';

		

END ;;
