mapper.select:
/* Bom.showBom */
SELECT A.prodCd, A.resrcCd, A.bomType, A.unitQty, A.flagYN, B.resrcCd, B.resrcNm, B.resrcType, B.protectedQty, B.maker, B.unit, B.option1, B.option2, B.quality, B.unitPrice, B.houseCd, B.compCd, B.barcode, C.houseNm, D.compNm, (IFNULL(E.inQty, 0) + IFNULL(F.outQty, 0)) qty FROM ( SELECT * FROM tblBom WHERE flagYN = 'Y' AND prodCd = #{prodCd} AND bomType = #{bomType} ) A LEFT JOIN ( SELECT * FROM tblResource WHERE flagYN = 'Y' ) B ON B.resrcCd = A.resrcCd LEFT JOIN ( SELECT * FROM tblHouse WHERE flagYN = 'Y' ) C ON C.houseCd = B.houseCd LEFT JOIN ( SELECT * FROM tblCompany WHERE flagYN = 'Y' ) D ON D.compCd = B.compCd LEFT JOIN ( SELECT IFNULL(SUM(qty), 0) inQty, resrcCd, MAX(issueDate) issueDate FROM tblResourceInOut WHERE flagYN = 'Y' AND planYN = 'N' AND qty > 0 GROUP BY resrcCd ) E ON E.resrcCd = B.resrcCd LEFT JOIN ( SELECT IFNULL(SUM(qty), 0) outQty, resrcCd, MAX(issueDate) issueDate FROM tblResourceInOut WHERE flagYN = 'Y' AND planYN = 'N' AND qty < 0 GROUP BY resrcCd ) F ON F.resrcCd = B.resrcCd
ORDER BY B.resrcNm
