mapper.select:
SELECT A.houseCd, A.houseNm FROM ( SELECT houseCd, houseNm FROM tblHouse WHERE flagYn = 'Y' ) A
WHERE A.houseNm LIKE CONCAT('%', #{findHouseNm}, '%')
ORDER BY A.houseCd ASC;

SELECT A.compCd, A.compNm FROM ( SELECT compCd, compNm FROM tblCompany WHERE flagYn = 'Y' ) A
WHERE A.compNm LIKE CONCAT('%', #{findCompNm}, '%')
ORDER BY A.compCd ASC;


SELECT
  A.compCd, A.compNm
FROM (
  SELECT compCd, compNm
  FROM tblCompany
  WHERE flagYn = 'Y'
) A



    WHERE A.compNm LIKE CONCAT('%', #{findCompNm}, '%')



      WHERE A.compCd = #{findCompCd}



ORDER BY A.compCd ASC;
