mapper.select:
/* ProductPlan.listProductInOutPlan */
SELECT A.inOutSeq, A.inOutDt, A.prodCd, A.qty, A.unitPrice, B.compNm, C.houseNm, ABS(qty*unitPrice) "supplyPrice", CASE WHEN A.qty < 0 THEN "출고예정" ELSE "입고예정" END "inOut" FROM ( SELECT * FROM tblProductInOut WHERE flagYN = 'Y' AND planYN = 'Y' AND prodCd != 0 AND prodCd = #{prodCd} ) A LEFT JOIN ( SELECT * FROM tblCompany WHERE flagYN = 'Y' ) B ON B.compCd = A.compCd LEFT JOIN ( SELECT * FROM tblHouse WHERE flagYN = 'Y' ) C ON C.houseCd = A.houseCd
ORDER BY A.inOutDt DESC, A.issueDate DESC
