mapper.select:
/* Report.listReportProdIn */
SELECT prodNm, SUM(`${curYear}-01`) '01', SUM(`${curYear}-02`) '02', SUM(`${curYear}-03`) '03', SUM(`${curYear}-04`) '04', SUM(`${curYear}-05`) '05', SUM(`${curYear}-06`) '06', SUM(`${curYear}-07`) '07', SUM(`${curYear}-08`) '08', SUM(`${curYear}-09`) '09', SUM(`${curYear}-10`) '10', SUM(`${curYear}-11`) '11', SUM(`${curYear}-12`) '12' FROM ( SELECT A.prodNm, IF (B.dt = '${curYear}-01', B.totalQty, 0) '${curYear}-01', IF (B.dt = '${curYear}-02', B.totalQty, 0) '${curYear}-02', IF (B.dt = '${curYear}-03', B.totalQty, 0) '${curYear}-03', IF (B.dt = '${curYear}-04', B.totalQty, 0) '${curYear}-04', IF (B.dt = '${curYear}-05', B.totalQty, 0) '${curYear}-05', IF (B.dt = '${curYear}-06', B.totalQty, 0) '${curYear}-06', IF (B.dt = '${curYear}-07', B.totalQty, 0) '${curYear}-07', IF (B.dt = '${curYear}-08', B.totalQty, 0) '${curYear}-08', IF (B.dt = '${curYear}-09', B.totalQty, 0) '${curYear}-09', IF (B.dt = '${curYear}-10', B.totalQty, 0) '${curYear}-10', IF (B.dt = '${curYear}-11', B.totalQty, 0) '${curYear}-11', IF (B.dt = '${curYear}-12', B.totalQty, 0) '${curYear}-12' FROM tblProduct A LEFT JOIN ( SELECT prodCd, LEFT (inOutDt, 7) dt, SUM(qty) totalQty FROM tblProductInOut WHERE qty > 0 AND flagYN = 'Y' GROUP BY LEFT (inOutDt, 7), prodCd ) B ON A.prodCd = B.prodCd ) Z
GROUP BY Z.prodNm ORDER BY Z.prodNm

/* Report.listReportResrcIn */
SELECT resrcNm, SUM(`${curYear}-01`) '01', SUM(`${curYear}-02`) '02', SUM(`${curYear}-03`) '03', SUM(`${curYear}-04`) '04', SUM(`${curYear}-05`) '05', SUM(`${curYear}-06`) '06', SUM(`${curYear}-07`) '07', SUM(`${curYear}-08`) '08', SUM(`${curYear}-09`) '09', SUM(`${curYear}-10`) '10', SUM(`${curYear}-11`) '11', SUM(`${curYear}-12`) '12' FROM ( SELECT A.resrcNm, IF (B.dt = '${curYear}-01', B.totalQty, 0) '${curYear}-01', IF (B.dt = '${curYear}-02', B.totalQty, 0) '${curYear}-02', IF (B.dt = '${curYear}-03', B.totalQty, 0) '${curYear}-03', IF (B.dt = '${curYear}-04', B.totalQty, 0) '${curYear}-04', IF (B.dt = '${curYear}-05', B.totalQty, 0) '${curYear}-05', IF (B.dt = '${curYear}-06', B.totalQty, 0) '${curYear}-06', IF (B.dt = '${curYear}-07', B.totalQty, 0) '${curYear}-07', IF (B.dt = '${curYear}-08', B.totalQty, 0) '${curYear}-08', IF (B.dt = '${curYear}-09', B.totalQty, 0) '${curYear}-09', IF (B.dt = '${curYear}-10', B.totalQty, 0) '${curYear}-10', IF (B.dt = '${curYear}-11', B.totalQty, 0) '${curYear}-11', IF (B.dt = '${curYear}-12', B.totalQty, 0) '${curYear}-12' FROM tblResource A LEFT JOIN ( SELECT resrcCd, LEFT (inOutDt, 7) dt, SUM(qty) totalQty FROM tblResourceInOut WHERE qty > 0 AND flagYN = 'Y' GROUP BY LEFT (inOutDt, 7), resrcCd ) B ON A.resrcCd = B.resrcCd ) Z
GROUP BY Z.resrcNm ORDER BY Z.resrcNm

/* Report.listReportProdOut */
